mapper.insert:
insert into bno_tmp(bno, batchno) select * from (
select #{item.BNO}, #{item.BATCH} from dual
) tmp

mapper.select:
select distinct * from (
	select bno, pbatch as batch
	from upload_idcode_temporary
	
		pbatch is not null
		 and id>=#{minId, jdbcType=INTEGER}
		 and id<=#{maxId, jdbcType=INTEGER}
	
	order by id asc
)
